mapper.insert:
insert into ticket values (   #{ticket_no}
							, #{bus_no}
							, #{seat_no}
							, #{hp}
							, #{age_group}
							, #{totalpay}
							, 'n'
							, sysdate
							, To_Date(#{arrive_day}, 'yyyy-mm-dd')
						  )

mapper.update:
update ticket set hp = #{hp}
				, bus_no = #{bus_no}
				, seat_no = #{seat_no}
				, age_group = #{age_group}
				, totalpay = #{totalpay}
				, arrive_day = #{arrive_day}
				, payday = #{payday} 
where ticket_no = #{ticket_no}
